import BookingInputForm from "../components/BookingInputForm";
import MapBooking from "../components/MapBooking";
import React from "react";
import { useState } from "react";
import TableBooking from "../components/TableBooking";
import vacationImg from "../assets/vacation.jpg";
import { Button, Toast, ToastContainer } from "react-bootstrap";

const Booking = () => {
  const [showTable, setShowTable] = useState(false);
  const element = document.getElementById("form");

  const setViewToMap = () => {
    element.scrollIntoView();
  };

  const showTableHandler = (bool) => {
    setShowTable(bool);
  };

  return (
    <div>
      <img src={vacationImg} alt="vacation" width="50%"></img>
      <div id="form">
        <BookingInputForm onShowTable={showTableHandler} />
      </div>
      <MapBooking />
      <ToastContainer
        className="p-3"
        position="bottom-center"
        style={{ zIndex: 1 }}
      >
        <Toast>
          <Toast.Header closeButton={false}>
            <img
              src="holder.js/20x20?text=%20"
              className="rounded me-2"
              alt=""
            />
            <strong className="me-auto">Bootstrap</strong>
            <small>11 mins ago</small>
          </Toast.Header>
          <Toast.Body>Hello, world! This is a toast message.</Toast.Body>
        </Toast>
      </ToastContainer>
      {showTable && <TableBooking onViewMap={setViewToMap} />}
    </div>
  );
};

export default Booking;
