import React from "react";
import classes from "./RowTableBooking.module.css";
import { Row, Col } from "react-bootstrap";
import { Button, Badge } from "react-bootstrap";
import { useDispatch } from "react-redux";
import { bookingActions } from "../store";

const RowTableBooking = (props) => {
  const dispatch = useDispatch();

  return (
    <div className={classes.row}>
      <Row className={classes.align}>
        <Col>
          <Button
            type="button"
            variant="outline-primary"
            onClick={() => {
              dispatch(
                bookingActions.uploadLocation({
                  location: [props.latitude, props.longitude],
                })
              );
            }}
          >
            Show on map
          </Button>
        </Col>
        <Col>
          <h3>{props.name}</h3>
          <p>{props.city}</p>
          <p>{props.address}</p>
          <a href={props.url} target="_blank">
            Link to Booking
          </a>
        </Col>
        <Col>
          <span>
            Number of adults: {props.adults}, children: {props.children}
          </span>
          <p>-------------</p>
          <h3>{props.price}â‚¬</h3>
        </Col>
        <Col>
          <h3>{props.reviewWord}</h3>
          <h1>
            <Badge bg="secondary">{props.review}</Badge>
          </h1>
          {props.reviewWord === "" && (
            <Badge bg="warning" text="dark">
              New to Booking.com
            </Badge>
          )}
        </Col>
      </Row>
    </div>
  );
};

export default RowTableBooking;
