import { useState, forwardRef, useImperativeHandle } from "react";
import Button from "react-bootstrap/Button";
import Modal from "react-bootstrap/Modal";
import ListGroup from "react-bootstrap/ListGroup";
import classes from "./SelectAirportModal.module.css";

const SelectAirportToModal = forwardRef((props, ref) => {
  const [show, setShow] = useState(false);
  let key = 1;

  const handleClose = () => setShow(false);
  useImperativeHandle(ref, () => ({
    handleShow() {
      setShow(true);
    },
  }));

  return (
    <Modal
      className={classes.modal}
      onHide={() => {
        props.setSelectedCity(null);
        handleClose();
      }}
      show={show}
      backdrop="static"
      keyboard={false}
    >
      <Modal.Header closeButton>
        <Modal.Title>Select airport</Modal.Title>
      </Modal.Header>
      <Modal.Body>
        <ListGroup>
          {props.airports !== [] &&
            props.airports.map((airport) => {
              return (
                <ListGroup.Item
                  key={key++}
                  action
                  variant="primary"
                  onClick={() => {
                    props.setSelectedCity(airport);
                    console.log(airport.name);
                  }}
                >
                  {airport.iata} | {airport.name}
                </ListGroup.Item>
              );
            })}
        </ListGroup>
      </Modal.Body>
      <Modal.Footer>
        <Button
          variant="secondary"
          onClick={() => {
            props.setSelectedCity(null);
            handleClose();
          }}
        >
          Close
        </Button>
        <Button
          variant="primary"
          disabled={props.selectedcity === null}
          onClick={() => {
            props.settingCitySelected(true);
            handleClose();
          }}
        >
          Select
        </Button>
      </Modal.Footer>
    </Modal>
  );
});

export default SelectAirportToModal;
