import { useState } from "react";
import { MapContainer, TileLayer, Marker, useMap } from "react-leaflet";
import styles from "./Map.module.css";
import { useSelector } from "react-redux";
import { Polyline } from "react-leaflet";

const Map = () => {
  const fromCoordinates = useSelector((state) => state.fromLoc.fromLocation);
  const isFromChanged = useSelector((state) => state.fromLoc.isFromChanged);
  const toCoordinates = useSelector((state) => state.toLoc.toLocation);
  const isToChanged = useSelector((state) => state.toLoc.isToChanged);
  const mp = useMap();

  const setMapView = () => {
    mp.setView(fromCoordinates, toCoordinates);
  };

  const MyComponent = () => {
    const map = useMapEvents({
      click: () => {
        map.setView(fromCoordinates, toCoordinates);
      },
    });
    return null;
  };

  return (
    <MapContainer
      className={styles.map}
      center={[51.4381, 5.4752]}
      zoom={2}
      scrollWheelZoom={false}
    >
      <MyComponent />
      <TileLayer
        maxZoom="20"
        minZoom="1"
        attribution='&copy; <a href="http://osm.org/copyright">OpenStreetMap</a> contributors'
        url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
      ></TileLayer>
      {isToChanged && <Marker position={toCoordinates} />}
      {isFromChanged && isToChanged && (
        <Polyline
          pathOptions={{ color: "blue" }}
          positions={[fromCoordinates, toCoordinates]}
        ></Polyline>
      )}
    </MapContainer>
  );
};

export const settingMap = setMapView;
export default Map;
