import React from "react";
import styles from "./TableBooking.module.css";
import RowTableBooking from "./RowTableBooking";
import { useDispatch, useSelector } from "react-redux";
import { bookingActions } from "../store";
import Form from "react-bootstrap/Form";

const TableBooking = (props) => {
  const properties = useSelector((state) => state.booking.properties);
  const dispatch = useDispatch();
  const [select, setSelect] = useState();

  return (
    <div className={styles.table}>
      <div className={styles.sort}>
        <h4>Sort by: </h4>
        <div className={styles.select}>
          <Form.Select aria-label="Select sorting">
            <option value="1">Best rated</option>
            <option
              value="2"
              onClick={() => {
                const apartments = properties.sort((a, b) => a.price - b.price);
                dispatch(
                  bookingActions.uploadProperties({ properties: apartments })
                );
              }}
            >
              Cheapest price
            </option>
          </Form.Select>
        </div>
      </div>
      {properties.map((property) => (
        <RowTableBooking
          key={property.id}
          name={property.name}
          city={property.city}
          latitude={property.latitude}
          longitude={property.longitude}
          address={property.address}
          review={property.review}
          price={property.price}
          checkout={property.checkout}
          checkin={property.checkin}
          url={property.url}
          photoUrl={property.photoUrl}
          reviewWord={property.reviewWord}
          adults={property.adults}
          children={property.children}
          showViewMap={props.onViewMap}
          onShowZoomModal={props.onShowZoomModal}
          type={property.type}
          checkin2={property.checkin2}
          checkout2={property.checkout2}
        ></RowTableBooking>
      ))}
    </div>
  );
};

export default TableBooking;
