import classes from "./InputForm.module.css";
import React, { useState } from "react";
import { useDispatch } from "react-redux";
import { fromLocationActions, toLocationActions } from "../store/index";
import { useSelector } from "react-redux";

const InputForm = () => {
  const todaysDate = () => {
    const date = new Date();

    let day = date.getDate();
    let month = date.getMonth() + 1;
    let year = date.getFullYear();

    if (month < 10) month = "0" + month;
    if (day < 10) day = "0" + day;

    const today = year + "-" + month + "-" + day;

    return today;
  };

  const dispatch = useDispatch();
  const [cityNameFrom, setCityNameFrom] = useState("");
  const [cityNameTo, setCityNameTo] = useState("");
  const [tripDate, setTripDate] = useState(todaysDate());
  const [fromApiLocation, setFromApiLocation] = useState(null);
  const [toApiLocation, setToApiLocation] = useState(null);
  const fromId = useSelector((state) => state.fromLoc.fromId);
  const toId = useSelector((state) => state.toLoc.toId);

  const optionsGeocoding = {
    method: "GET",
    headers: {
      "X-Api-Key": "w/ji57sNSaUz/bxWAIxJ3A==UmFkltyZkWVv9tvU",
    },
  };

  function fetchFromLocationHandler(cityName) {
    fetch(
      `https://api.api-ninjas.com/v1/geocoding?city=${cityName}`,
      optionsGeocoding
    )
      .then((response) => response.json())
      .then((data) => {
        console.log(
          data[0].latitude + " i " + data[0].longitude + " from lokacija !"
        );
        const lon = data[0].longitude;
        const lat = data[0].latitude;
        dispatch(fromLocationActions.uploadLocation({ location: [lat, lon] }));
        return [lat, lon];
      })
      .catch((err) => console.error(err));
  }

  function fetchToLocationHandler(cityName) {
    fetch(
      `https://api.api-ninjas.com/v1/geocoding?city=${cityName}`,
      optionsGeocoding
    )
      .then((response) => response.json())
      .then((data) => {
        console.log(
          data[0].latitude + " i " + data[0].longitude + " to lokacija !"
        );
        const lon = data[0].longitude;
        const lat = data[0].latitude;
        dispatch(toLocationActions.uploadLocation({ location: [lat, lon] }));
        return [lat, lon];
      })
      .catch((err) => console.error(err));
  }

  const optionsAutocomplete = {
    method: "GET",
    headers: {
      "X-RapidAPI-Key": "b1ea854598mshf9e4713004ceb47p17ac51jsna4065fbbf64a",
      "X-RapidAPI-Host": "flixbus2.p.rapidapi.com",
    },
  };

  function fetchAutocompleteFromCity(cityName) {
    fetch(
      `https://flixbus2.p.rapidapi.com/autocomplete?query=${cityName}`,
      optionsAutocomplete
    )
      .then((response) => response.json())
      .then((data) => {
        const city_id = data[0].city.legacy_id;
        console.log("city from id -> " + city_id);
        dispatch(fromLocationActions.uploadCityId({ id: city_id }));
        return city_id;
      })
      .catch((err) => console.error(err));
  }

  function fetchAutocompleteToCity(cityName) {
    fetch(
      `https://flixbus2.p.rapidapi.com/autocomplete?query=${cityName}`,
      optionsAutocomplete
    )
      .then((response) => response.json())
      .then((data) => {
        const city_id = data[0].city.legacy_id;
        console.log("city to id -> " + city_id);
        dispatch(toLocationActions.uploadCityId({ id: city_id }));
        return city_id;
      })
      .catch((err) => console.error(err));
  }

  const optionsSearchTrips = {
    method: "GET",
    headers: {
      "X-RapidAPI-Key": "b1ea854598mshf9e4713004ceb47p17ac51jsna4065fbbf64a",
      "X-RapidAPI-Host": "flixbus.p.rapidapi.com",
    },
  };

  function searchFlixTrips() {
    const fromCityId = fetchAutocompleteFromCity(cityNameFrom);
    const toCityId = fetchAutocompleteToCity(cityNameTo);

    fetch(
      `https://flixbus.p.rapidapi.com/v1/search-trips?to_id=${toCityId}&from_id=${fromCityId}&currency=EUR&departure_date=${tripDate}&number_adult=1&search_by=cities`,
      optionsSearchTrips
    )
      .then((response) => response.json())
      .then((data) => {
        const trips = data[0].items;
        console.log("Trips found: " + trips);
        return trips;
      })
      .catch((err) => console.error(err));
  }

  const btnActionHandler = () => {
    const fromLocation = fetchFromLocationHandler(cityNameFrom);
    const toLocation = fetchToLocationHandler(cityNameTo);
    setFromApiLocation(fromLocation);
    setToApiLocation(toLocation);

    // fetchAutocompleteFromCity(cityNameFrom);

    // fetchAutocompleteToCity(cityNameTo);

    const trips = searchFlixTrips();

    console.log("Linije su: " + trips);
  };

  return (
    <form className={classes.form}>
      <div className={classes.select__from}>
        <input
          // placeholder="From: city"
          onChange={(event) => {
            setCityNameFrom(event.target.value);
          }}
        ></input>
      </div>
      <div className={classes.select__to}>
        <input
          // placeholder="To: city"

          onChange={(event) => {
            setCityNameTo(event.target.value);
          }}
        ></input>
      </div>
      <div className={classes.datepicker}>
        <input
          type="date"
          onChange={(event) => {
            setTripDate(event.target.value);
          }}
          //placeholder={todaysDate}
          defaultValue={todaysDate.toString}
          // min={todaysDate.toString}
        ></input>
      </div>
      <div className={classes.button__search}>
        <button type="button" onClick={btnActionHandler}>
          Search
        </button>
      </div>
    </form>
  );
};

export default InputForm;
