import { useState } from "react";
import { MapContainer, TileLayer, Marker } from "react-leaflet";
import styles from "./Map.module.css";
import { fromCoordinates, toCoordinates } from "../store";
import { useSelector } from "react-redux";
import "leaflet-polylinedecorator";
import L from "leaflet";

const Map = () => {
  const fromCoordinates = useSelector((state) => state.fromLoc.fromLocation);
  const isFromChanged = useSelector((state) => state.fromLoc.isFromChanged);
  const toCoordinates = useSelector((state) => state.toLoc.toLocation);
  const isToChanged = useSelector((state) => state.toLoc.isToChanged);
  const polyline = [fromCoordinates, toCoordinates];
  const arrow = [
    {
      offset: "100%",
      repeat: 0,
      symbol: L.Symbol.arrowHead({
        pixelSize: 15,
        polygon: false,
        pathOptions: { stroke: true },
      }),
    },
  ];

  return (
    <MapContainer
      className={styles.map}
      center={[51.4381, 5.4752]}
      zoom={2}
      scrollWheelZoom={false}
    >
      <TileLayer
        maxZoom="20"
        minZoom="1"
        attribution='&copy; <a href="http://osm.org/copyright">OpenStreetMap</a> contributors'
        url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
      >
        {{ toCoordinates } && <Marker position={toCoordinates}></Marker>}
      </TileLayer>
      {isFromChanged && <Marker position={fromCoordinates} />}
      {isToChanged && <Marker position={toCoordinates} />}
      {isFromChanged && isToChanged && (
        <PolylineDecorator
          patterns={arrow}
          positions={polyline}
        ></PolylineDecorator>
      )}
    </MapContainer>
  );
};

export default Map;
