import { useState } from "react";
import { MapContainer, TileLayer, Marker } from "react-leaflet";
import styles from "./Map.module.css";
import { fromCoordinates, toCoordinates } from "../store";
import { useSelector } from "react-redux";

const Map = () => {
  const [markerFrom, setMarkerFrom] = useState(null);
  const [markerTo, setMarkerTo] = useState(null);
  const fromCoordinates = useSelector((state) => state.fromLoc.fromLocation);
  const isFromChanged = useSelector((state) => state.fromLoc.isFromChanged);

  return (
    <MapContainer
      className={styles.map}
      center={[51.4381, 5.4752]}
      zoom={2}
      scrollWheelZoom={false}
    >
      <TileLayer
        maxZoom="20"
        minZoom="1"
        attribution='&copy; <a href="http://osm.org/copyright">OpenStreetMap</a> contributors'
        url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
      >
        {{ toCoordinates } && <Marker position={toCoordinates}></Marker>}
      </TileLayer>
      {isFromChanged && <Marker position={fromCoordinates} />}
    </MapContainer>
  );
};

export default Map;
