import Map from "../components/Map";
import mapStyle from "./Leaflet.module.css";
import InputForm from "../components/InputForm";
import styles from "./Pages.module.css";
import React, { useRef, useEffect, useState } from "react";
import Table from "../components/Table";

const Buses = () => {
  const tableRef = useRef();
  const [scrolls, setScrolls] = useState([]);

  useEffect(() => {
    tableRef.current?.scrollIntoView({ behavior: "smooth" });
  }, [scrolls]);

  const scrollChangeHandler = () => {
    setScrolls((current) => [...current, "scrolling"]);
  };

  return (
    <div>
      <h1>Bus routes Page</h1>
      <div className={styles.body}>
        <InputForm onChangeScroll={scrollChangeHandler} />
        <Map className={mapStyle["leaflet-container"]} />
        <Table />
      </div>
    </div>
  );
};

export default Buses;
