import { useState, forwardRef, useImperativeHandle } from "react";
import Button from "react-bootstrap/Button";
import Modal from "react-bootstrap/Modal";
import ListGroup from "react-bootstrap/ListGroup";
import classes from "./SelectAirportModal.module.css";

const SelectAirportFromModal = forwardRef((props, ref) => {
  const [show, setShow] = useState(false);

  const airports = props.airports;

  const handleClose = () => setShow(false);
  useImperativeHandle(ref, () => ({
    handleShow() {
      setShow(true);
    },
  }));

  return (
    <Modal
      className={classes.modal}
      show={show}
      onHide={handleClose}
      backdrop="static"
      keyboard={false}
    >
      <Modal.Header closeButton>
        <Modal.Title>Select airport</Modal.Title>
      </Modal.Header>
      <Modal.Body>
        <ListGroup>
          {airports.forEach((airport) => {
            <ListGroup.Item action>
              {airport.name} | {airport.iata}
            </ListGroup.Item>;
          })}
        </ListGroup>
      </Modal.Body>
      <Modal.Footer>
        <Button variant="secondary" onClick={handleClose}>
          Close
        </Button>
        <Button variant="primary">Select</Button>
      </Modal.Footer>
    </Modal>
  );
});

export default SelectAirportFromModal;
