import React from "react";
import styles from "./Table.module.css";
import Button from "react-bootstrap/Button";
import { useSelector, useDispatch } from "react-redux";
import { favoritesActions } from "../store";

const RowTableFlights = (props) => {
  const favorites = useSelector((state) => state.favorites.favorites);
  const dispatch = useDispatch(favoritesActions.uploadFavorites({}));

  return (
    <div className={styles.row}>
      <div className={styles.column_text}>
        <span>{props.departureDate}</span>
      </div>
      <div className={styles.column_text}>
        <span>{props.departureTime}h</span>
        <span>
          {props.airportFrom.iata} | {props.airportFrom.name}
        </span>
      </div>
      <div className={styles.column_text}>
        <span>{props.arrivalTime}h</span>
        <span>
          {props.airportTo.iata} | {props.airportTo.name}
        </span>
      </div>
      <div className={styles.column_text}>
        <span>Flight number:</span>
        <span>{props.number}</span>
      </div>
      <div className={styles.column_text}>
        <span>Operator:</span>
        <span>{props.airline}</span>
      </div>
      <div className="add_button">
        <Button type="button" onClick={dispatch()}>
          <svg
            xmlns="http://www.w3.org/2000/svg"
            width="24"
            height="24"
            fill="currentColor"
            className="bi bi-heart"
            viewBox="0 0 16 16"
          >
            <path d="m8 2.748-.717-.737C5.6.281 2.514.878 1.4 3.053c-.523 1.023-.641 2.5.314 4.385.92 1.815 2.834 3.989 6.286 6.357 3.452-2.368 5.365-4.542 6.286-6.357.955-1.886.838-3.362.314-4.385C13.486.878 10.4.28 8.717 2.01L8 2.748zM8 15C-7.333 4.868 3.279-3.04 7.824 1.143c.06.055.119.112.176.171a3.12 3.12 0 0 1 .176-.17C12.72-3.042 23.333 4.867 8 15z" />
          </svg>
        </Button>
      </div>
    </div>
  );
};

export default RowTableFlights;
