import classes from "./InputForm.module.css";
import React, { useState, useCallback } from "react";

const InputForm = (props) => {
  const todaysDate = () => {
    const date = new Date();

    let day = date.getDate();
    let month = date.getMonth() + 1;
    let year = date.getFullYear();

    if (month < 10) month = "0" + month;
    if (day < 10) day = "0" + day;

    const today = year + "-" + month + "-" + day;

    return today;
  };

  const [cityNameFrom, setCityNameFrom] = useState("");
  const [cityNameTo, setCityNameTo] = useState("");
  const [apiLocation, setApiLocation] = useState({ lon: "", lan: "" });
  const [error, setError] = useState(null);

  const options = {
    method: "GET",
    headers: {
      "X-RapidAPI-Key": "b1ea854598mshf9e4713004ceb47p17ac51jsna4065fbbf64a",
      "X-RapidAPI-Host": "flixbus2.p.rapidapi.com",
    },
  };

  async function fetchLocationHandler(cityName) {
    try {
      const response = await fetch(
        `https://flixbus2.p.rapidapi.com/autocomplete?query=${cityName}`,
        options
      );
      if (!response.ok) {
        throw new Error("Something went wrong!");
      }

      const data = await response.json();

      const apiLocations = data.map((locationData) => {
        return {
          lon: locationData.lon,
          lan: locationData.lan,
        };
      });

      setApiLocation(apiLocations);
      console.log(
        apiLocation.lon + " i " + apiLocation.lan + " ---- " + apiLocation
      );
    } catch (error) {
      setError(error.message);
    }
  }

  const btnActionHandler = () => {
    fetchLocationHandler(cityNameFrom);
    // fetchLocationHandler(cityNameTo);
    console.log("Lokacija" + apiLocation.lon + ", " + apiLocation.lan);
  };

  return (
    <form className={classes.form}>
      <div className={classes.select__from}>
        <span>&nbsp;TRAVEL FROM</span>
        <input
          // placeholder="From: city"
          onChange={(event) => {
            setCityNameFrom(event.target.value);
          }}
        ></input>
      </div>
      <div className={classes.select__to}>
        <span>&nbsp;TRAVEL TO</span>
        <input
          // placeholder="To: city"
          onChange={(event) => {
            setCityNameTo(event.target.value);
          }}
        ></input>
      </div>
      <div className={classes.datepicker}>
        <span>&nbsp;SELECT DATE</span>
        <input
          type="date"
          // placeholder={todaysDate}
          defaultValue={todaysDate.toString}
          // min={todaysDate.toString}
        ></input>
      </div>
      <div className={classes.button__search}>
        <span></span>
        <button onClick={btnActionHandler}>Search</button>
      </div>
    </form>
  );
};

export default InputForm;
