import { createSlice } from "@reduxjs/toolkit";
import { configureStore } from "@reduxjs/toolkit/dist/configureStore";

const fromState = {
  location: null,
  isChanged: false,
};

const toState = {
  location: null,
  isChanged: false,
};

const fromSlice = createSlice({
  name: "fromLocation",
  fromState,
  reducers: {
    uploadLocation(state, action) {
      state.location = action.payload.location;
    },
  },
});

const toSlice = createSlice({
  name: "toLocation",
  toState,
  reducers: {
    uploadLocation(state, action) {
      state.location = action.payload.location;
    },
  },
});

const store = configureStore({
  reducer: { fromLoc: fromSlice.reducer, toLoc: toSlice.reducer },
});

export const toLocationActions = toSlice.actions;
export const fromLocationActions = fromSlice.actions;
export default store;
