import { useSelector } from "react-redux";
import { Tab, Tabs } from "react-bootstrap";
import classes from "./Favorites.module.css";

const Favorites = () => {
  const favorites = useSelector((state) => state.favorites.favorites);

  const flights = favorites.filter((obj) => {
    return obj.type === "flight";
  });

  const buses = favorites.filter((obj) => {
    return obj.type === "bus";
  });

  const rooms = favorites.filter((obj) => {
    return obj.type === "rooms";
  });

  const rentals = favorites.filter((obj) => {
    return obj.type === "rental";
  });

  return (
    <div className={classes.favorites}>
      <div>
        <h2>YOUR FAVORITES LIST</h2>
        <h4>Check list of your saved offers.</h4>
      </div>
      <div className={classes.tabs}>
        <Tabs defaultActiveKey="profile" id="12345" className="mb-3" justify>
          <Tab eventKey="flights" title="Flights">
            {flights.length === 0 ? (
              <p>List is empty.</p>
            ) : (
              flights.map((el) => {
                return (
                  <div className={styles.row}>
                    <div className={styles.column_text}>
                      <span>{props.departureDate}</span>
                    </div>
                    <div className={styles.column_text}>
                      <span>{props.departureTime}h</span>
                      <span>
                        {props.airportFrom.iata} | {props.airportFrom.name}
                      </span>
                    </div>
                    <div className={styles.column_text}>
                      <span>{props.arrivalTime}h</span>
                      <span>
                        {props.airportTo.iata} | {props.airportTo.name}
                      </span>
                    </div>
                    <div className={styles.column_text}>
                      <span>Flight number:</span>
                      <span>{props.number}</span>
                    </div>
                    <div className={styles.column_text}>
                      <span>Operator:</span>
                      <span>{props.airline}</span>
                    </div>
                  </div>
                );
              })
            )}
          </Tab>
          <Tab eventKey="bus" title="Bus">
            Tab content for Profile
          </Tab>
          <Tab eventKey="rooms" title="Rooms">
            Tab content for Contact
          </Tab>
          <Tab eventKey="rental" title="Rent-a-car">
            Tab content for Contact
          </Tab>
        </Tabs>
      </div>
      {favorites.map((el) => {
        return <p>da da</p>;
      })}
    </div>
  );
};

export default Favorites;
