import { DependencyList } from "react";
declare type HookWithDependencies<C, R> = (callback: C, deps: DependencyList) => R;
declare const useGranularHook: <T extends HookWithDependencies<C, ReturnType<T>>, C>(hook: T, callback: C, primaryDeps: DependencyList, secondaryDeps: DependencyList) => ReturnType<T>;
export default useGranularHook;
